import styled from "styled-components";
import backgroundImage from '../../img/bg-home.jpg'

const Container = styled.div`
width: 100%;
    background-image: url(${backgroundImage});
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
`;

export {Container}
