import styled from "styled-components";

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(17, 18, 19, 0.4);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContent = styled.div`
  background: var(--main-white-color);
  width: 982px;
  height: 720px;
  padding: 40px;
  border-radius: 20px;
  z-index: 100;
`;

const TitleWrapper = styled.div`
display: flex;
justify-content: space-between;
margin-bottom: 10px;
`;

const Title = styled.p`
font-size: 24px;
font-weight: 600;
line-height: 1.25; 
`;

const CloseBtn = styled.button`
border: none;
background-color: inherit;
`;

const RatingContainer = styled.div`
display: flex;
gap: 16px;
margin-bottom: 24px;
font-size: 16px;
line-height: 1.5; 
margin-bottom: 16px;
`;

const Wrapper = styled.div`
display: flex;
align-items: center;
gap: 4px;
`;

const Text = styled.p`
text-decoration-line: underline;
`;

const Description = styled.p``;

const ImgList = styled.ul`
display: flex;
gap: 16px;
`;

const ImgItem = styled.li`
width: calc(100% / 3 - 16 * 2);
height: 310px;

`;

const Img = styled.img`
width: 100%;
height: 100%;
object-fit: fill;
border-radius: 10px;
`;

export {
  Overlay,
  ModalContent,
  TitleWrapper,
  Title,
  CloseBtn,
  Wrapper,
  Text,
  Description,
  RatingContainer,
  ImgList,
  Img,
  ImgItem
};
