import styled from "styled-components";
import backgroundImage from '../../img/bg-home.jpg'

const Container = styled.div`
max-width: 1440px;
    background-image: url(${backgroundImage});
    background-repeat: no-repeat;
    background-size: contain;
    height: 716px;
`;

export {Container}
