import axios from 'axios';
import { createAsyncThunk } from '@reduxjs/toolkit';

axios.defaults.baseURL = 'https://65742b4af941bda3f2af6ecf.mockapi.io/';

const TRAILERS_PER_PAGE = 4;

export const fetchTrailers = createAsyncThunk(
  'trailers/fetchAll',
  async (page, thunkAPI) => {
    try {
        const offset = (page - 1) * TRAILERS_PER_PAGE;
      const response = await axios.get(`/advert?_start=${offset}&_limit=${TRAILERS_PER_PAGE}`);
      return response.data;
    } catch (error) {
      thunkAPI.rejectWithValue(error.message);
    }
  }
);
