import React, { useState } from "react";
import {
  ModalContent,
  Overlay,
  TitleWrapper,
  RatingContainer,
  Title,
  CloseBtn,
  Wrapper,
  Text,
  Description,
  ImgList,
  Img,
  ImgItem
} from "./Modal.styled";
import { Icon } from "../Icon/Icon";

export const Modal = ({ show, close, trailer }) => {
  if (!show) {
    return null;
  }

  return (
    <Overlay onClick={close}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <TitleWrapper>
          <Title>{trailer.trailer.name}</Title>
          <CloseBtn type="button" onClick={close}>
            <Icon iconid={"icon-close"} width={32} height={32} />
          </CloseBtn>
        </TitleWrapper>
        <RatingContainer>
          <Wrapper>
            <Icon iconid={"icon-star-yellow"} width={16} height={16} />
            <Text>
              {trailer.trailer.rating}({trailer.trailer.reviews.length} Reviews)
            </Text>
          </Wrapper>
          <Wrapper>
            <Icon iconid={"icon-map-pin"} width={16} height={16} />
            <p>{trailer.trailer.location}</p>
          </Wrapper>
        </RatingContainer>
        <Title>â‚¬{trailer.trailer.price}</Title>
        <ImgList>
          {trailer.trailer.gallery.map((img) => {
            return (
              <ImgItem key={img} imgSrc={img}>
                <Img src={img} alt="camper image" />
              </ImgItem>
            );
          })}
        </ImgList>
        <Description>{trailer.trailer.description}</Description>
        <div>
          <div>Features/Reviews</div>
          <div>Form</div>
        </div>
      </ModalContent>
    </Overlay>
  );
};
